import React, { useState, useRef, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import VoiceRecorderButton from './components/VoiceRecorderButton';
import './App.css';

function ChatBubble({ text, type }) {
  return (
    <div className={`flex ${type === 'user' ? 'justify-end' : 'justify-start'} mb-4`}>
      <div className={`max-w-lg px-5 py-3 rounded-2xl shadow-lg text-base font-medium ${type === 'user' ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-100'} animate-fade-in`} style={{ fontFamily: 'Poppins, sans-serif' }}>
        {text}
      </div>
    </div>
  );
}

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [chat, setChat] = useState([]); // {type: 'user'|'ai', text: string}
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const chatEndRef = useRef(null);

  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [chat]);

  const handleVoiceInput = async (audioBlob) => {
    setLoading(true);
    setError('');
    try {
      // Send audio to backend /stt
      const sttRes = await fetch('/api/stt', {
        method: 'POST',
        body: audioBlob
      });
      const sttData = await sttRes.json();
      if (sttData.error) throw new Error(sttData.error);
      setChat((prev) => [...prev, { type: 'user', text: sttData.text }]);
      // Send text to backend /generate
      const genRes = await fetch('/api/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query: sttData.text })
      });
      const genData = await genRes.json();
      if (genData.error) throw new Error(genData.error);
      // Gradually reveal AI response
      let aiText = '';
      const words = genData.explanation.split(' ');
      for (let i = 0; i < words.length; i++) {
        aiText += (i === 0 ? '' : ' ') + words[i];
        setChat((prev) => {
          const last = prev[prev.length - 1];
          if (last && last.type === 'ai') {
            return [...prev.slice(0, -1), { type: 'ai', text: aiText }];
          } else {
            return [...prev, { type: 'ai', text: aiText }];
          }
        });
        await new Promise((r) => setTimeout(r, 40));
      }
      // Add code as a separate bubble
      setChat((prev) => [...prev, { type: 'ai', text: genData.code }]);
    } catch (err) {
      setError(err.message || 'Error fetching result');
    }
    setLoading(false);
  };

  // Sidebar width for mic button adjustment
  const sidebarWidth = sidebarOpen ? 256 : 80; // Tailwind w-64 = 256px, w-20 = 80px

  return (
    <div className="flex min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-700" style={{ fontFamily: 'Poppins, sans-serif' }}>
      <Sidebar open={sidebarOpen} onToggle={() => setSidebarOpen((o) => !o)} />
      <main className="flex-1 flex flex-col items-center justify-start p-0 relative">
        <div className="w-full flex flex-col items-center pt-12 pb-4">
          <img src="/assets/app_icon.png" alt="App Icon" className="w-20 h-20 mb-4 drop-shadow-lg" />
          <h1 className="text-5xl font-extrabold text-white mb-2 tracking-tight drop-shadow-lg">EchoCode.ai</h1>
          <p className="text-2xl text-gray-300 font-semibold mb-8 drop-shadow">Your voice-powered programming assistant. Ask for code, explanations, and more!</p>
        </div>
        <div className="w-full max-w-2xl flex flex-col gap-2 bg-transparent rounded-xl shadow-none p-0 mt-2 overflow-y-auto" style={{ minHeight: '400px', maxHeight: '60vh' }}>
          {chat.map((msg, idx) => (
            <ChatBubble key={idx} text={msg.text} type={msg.type} />
          ))}
          <div ref={chatEndRef} />
        </div>
        {error && <div className="text-red-500 mt-2 font-bold">{error}</div>}
        {/* Absolutely centered mic button at bottom of main content area */}
        <div className="absolute left-1/2 bottom-10 transform -translate-x-1/2 z-50">
          <VoiceRecorderButton onVoiceInput={handleVoiceInput} loading={loading} floating />
        </div>
        <div className="mt-8 text-center text-gray-400 text-xs">EchoCode.AI &copy; 2025</div>
      </main>
    </div>
  );
}

export default App;
