import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import VoiceRecorderButton from './components/VoiceRecorderButton';
import QueryDisplay from './components/QueryDisplay';
import ResultDisplay from './components/ResultDisplay';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleVoiceInput = async (audioBlob) => {
    setLoading(true);
    setError('');
    try {
      // Send audio to backend /stt
      const sttRes = await fetch('/api/stt', {
        method: 'POST',
        body: audioBlob
      });
      const sttData = await sttRes.json();
      setQuery(sttData.text);
      // Send text to backend /generate
      const genRes = await fetch('/api/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query: sttData.text })
      });
      const genData = await genRes.json();
      setResult(genData);
    } catch (err) {
      setError('Error processing your request.');
    }
    setLoading(false);
  };

  return (
    <div className="flex min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-700" style={{ fontFamily: 'Poppins, sans-serif' }}>
      <Sidebar />
      <main className="flex-1 flex flex-col items-center justify-start p-0 relative">
        <div className="w-full flex flex-col items-center pt-12 pb-4">
          <img src="/assets/app_icon.png" alt="App Icon" className="w-20 h-20 mb-4 drop-shadow-lg" />
          <h1 className="text-5xl font-extrabold text-white mb-2 tracking-tight drop-shadow-lg">EchoCode.ai</h1>
          <p className="text-2xl text-gray-300 font-semibold mb-8 drop-shadow">Your voice-powered programming assistant. Ask for code, explanations, and more!</p>
        </div>
        <div className="w-full max-w-2xl bg-gray-900 bg-opacity-80 rounded-xl shadow-2xl p-8 mt-2">
          <QueryDisplay query={query} />
          {error && <div className="text-red-500 mt-2 font-bold">{error}</div>}
          {result && <ResultDisplay explanation={result.explanation} code={result.code} language={result.language} />}
        </div>
        {/* Floating mic button always centered at bottom */}
        <div className="fixed bottom-10 left-1/2 transform -translate-x-1/2 z-50">
          <VoiceRecorderButton onVoiceInput={handleVoiceInput} loading={loading} floating />
        </div>
        <div className="mt-8 text-center text-gray-400 text-xs">EchoCode.AI &copy; 2025</div>
      </main>
    </div>
  );
}

export default App;
