import React from 'react';

function Sidebar() {
  return (
    <div className="flex flex-col h-screen w-64 bg-gradient-to-b from-gray-900 to-gray-800 text-white shadow-lg">
      <div className="p-6 border-b border-gray-700">
        <h2 className="text-2xl font-bold tracking-wide">EchoCode.AI</h2>
        <span className="text-xs text-gray-400">Voice Programming Assistant</span>
      </div>
      <nav className="flex-1 p-6 space-y-4">
        <a href="#" className="block py-2 px-4 rounded hover:bg-gray-700 font-semibold">Home</a>
        <a href="#" className="block py-2 px-4 rounded hover:bg-gray-700 font-semibold">History</a>
        <a href="#" className="block py-2 px-4 rounded hover:bg-gray-700 font-semibold">About</a>
      </nav>
      <div className="p-6 border-t border-gray-700 flex items-center justify-between">
        <div className="flex items-center">
          <img src="https://ui-avatars.com/api/?name=User" alt="User" className="w-10 h-10 rounded-full border-2 border-gray-600" />
          <span className="ml-3 font-medium">You</span>
        </div>
      </div>
    </div>
  );
}

export default Sidebar;
