import React, { useState } from 'react';
import { FiHome, FiClock, FiInfo, FiUser } from 'react-icons/fi';

const navItems = [
  { icon: <FiHome size={22} />, text: 'Home' },
  { icon: <FiClock size={22} />, text: 'History' },
  { icon: <FiInfo size={22} />, text: 'About' },
];

function Sidebar() {
  const [open, setOpen] = useState(true);

  return (
    <div className={`flex flex-col h-screen ${open ? 'w-64' : 'w-20'} bg-gradient-to-b from-gray-900 to-gray-800 text-white shadow-lg transition-all duration-300`} style={{ fontFamily: 'Poppins, sans-serif' }}>
      <div className="flex items-center justify-between p-4 border-b border-gray-700">
        <button
          onClick={() => setOpen(!open)}
          className="focus:outline-none"
          style={{ transition: 'transform 0.3s' }}
        >
          <img
            src="/assets/app_icon.png"
            alt="App Icon"
            className={`w-8 h-8 drop-shadow ${open ? 'rotate-0' : 'rotate-90'} transition-transform duration-300`}
            style={{ transform: open ? 'rotate(0deg)' : 'rotate(90deg)' }}
          />
        </button>
        {open && <span className="font-bold text-xl tracking-wide ml-2">EchoCode.AI</span>}
      </div>
      <nav className="flex-1 p-4 space-y-2">
        {navItems.map((item, idx) => (
          <a key={idx} href="#" className={`flex items-center py-2 px-3 rounded hover:bg-gray-700 font-semibold transition-all duration-200 ${open ? '' : 'justify-center'}`}> 
            {item.icon}
            {open && <span className="ml-3">{item.text}</span>}
          </a>
        ))}
      </nav>
      <div className="p-4 border-t border-gray-700 flex items-center justify-center mt-auto">
        <div className="flex items-center">
          <FiUser size={28} />
          {open && <span className="ml-3 font-medium">You</span>}
        </div>
      </div>
    </div>
  );
}

export default Sidebar;
