import React, { useState, useRef, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import VoiceRecorderButton from './components/VoiceRecorderButton';
import './App.css';

function ChatBubble({ text, type }) {
  return (
    <div className={`flex ${type === 'user' ? 'justify-end' : 'justify-start'} mb-4`}>
      <div className={`max-w-lg px-5 py-3 rounded-2xl shadow-lg text-base font-medium break-words ${type === 'user' ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-100'} animate-fade-in`} style={{ fontFamily: 'Poppins, sans-serif' }}>
        {text}
      </div>
    </div>
  );
}

const SIDEBAR_WIDTH = 256; // px matching w-64

const SIDEBAR_SECTIONS = [
  { key: 'home', label: 'Home' },
  { key: 'new', label: 'New Chat' },
  { key: 'history', label: 'History' },
  { key: 'about', label: 'About' },
];

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [chat, setChat] = useState(() => JSON.parse(localStorage.getItem('echocode_chat')) || []);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [section, setSection] = useState('home');
  const [textInput, setTextInput] = useState('');
  const chatEndRef = useRef(null);
  const [headerAtTop, setHeaderAtTop] = useState(false);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    localStorage.setItem('echocode_chat', JSON.stringify(chat));
    setHeaderAtTop(chat.length > 0);
  }, [chat]);

  // Gemini logic: Keep exactly your existing async functions etc
  async function handleVoiceInput(audioBlob) {
    setLoading(true);
    setError('');
    try {
      /* existing Gemini STT and generate fetch logic */
      // ...
    } catch (err) {
      setError(err.message || 'Error fetching result');
    }
    setLoading(false);
  }

  async function handleTextInput() {
    if (!textInput.trim()) return;
    setLoading(true);
    setError('');
    try {
      /* existing Gemini generate fetch logic */
      // ...
      setTextInput('');
    } catch (err) {
      setError(err.message || 'Error fetching result');
    }
    setLoading(false);
  }

  function handleFileInput(e) {
    const file = e.target.files?.[0];
    if (!file) return;
    setChat(prev => [...prev, { type: 'user', text: `Uploaded file: ${file.name}` }]);
    setError('File upload functionality is not implemented yet');
  }

  function handleSectionClick(key) {
    setSection(key);
    if (key === 'new') {
      setChat([]);
      setHeaderAtTop(false);
      localStorage.setItem('echocode_chat', JSON.stringify([]));
    }
  }

  return (
    <div className="relative font-poppins min-h-screen flex bg-gradient-to-br from-gray-900 via-gray-800 to-gray-700">
      {/* Sidebar container */}
      <aside
        className={`fixed top-0 left-0 h-full bg-gray-900 transition-width duration-500 shadow-lg z-50 overflow-hidden`}
        style={{ width: sidebarOpen ? SIDEBAR_WIDTH : 0 }}
        aria-expanded={sidebarOpen}
      >
        {sidebarOpen && (
          <>
            <Sidebar
              open={sidebarOpen}
              onToggle={() => setSidebarOpen(o => !o)}
              sections={SIDEBAR_SECTIONS}
              onSectionClick={handleSectionClick}
              activeSection={section}
              className="h-full"
            />
            {/* Close toggle with app icon */}
            <button
              onClick={() => setSidebarOpen(false)}
              aria-label="Close sidebar"
              className="absolute top-4 right-[-40px] w-14 h-14 bg-gray-800 rounded-full flex items-center justify-center shadow-lg border border-gray-700 hover:bg-gray-700 transition"
            >
              <img src="/assets/app_icon.png" alt="Close Sidebar" className="w-8 h-8" />
            </button>
          </>
        )}
      </aside>

      {/* Main section shifts according to sidebar width */}
      <main
        className="flex-1 flex flex-col overflow-hidden relative transition-margin duration-500"
        style={{ marginLeft: sidebarOpen ? SIDEBAR_WIDTH : 0 }}
      >
        {/* Sidebar open toggle button visible only when sidebar is closed */}
        {!sidebarOpen && (
          <button
            onClick={() => setSidebarOpen(true)}
            aria-label="Open sidebar"
            className="fixed top-4 left-4 z-50 w-14 h-14 bg-gray-800 rounded-full flex items-center justify-center shadow-lg border border-gray-700 hover:bg-gray-700 transition"
          >
            <img src="/assets/app_icon.png" alt="Open Sidebar" className="w-8 h-8" />
          </button>
        )}

        {/* Content wrapper */}
        <div className="flex-1 flex flex-col max-w-4xl mx-auto px-4 pt-4 pb-0" style={{ minHeight: 0 }}>
          {/* Header */}
          <header className={`flex flex-col items-center text-center transition-all duration-700 ${headerAtTop ? 'pt-8' : 'pt-24'}`}>
            <img src="/assets/app_icon.png" alt="App Icon" className="w-16 h-16 md:w-20 md:h-20 mb-4 drop-shadow-lg" />
            <h1 className="text-3xl md:text-5xl font-extrabold text-white mb-2 tracking-tight drop-shadow-lg">EchoCode.ai</h1>
            <p className="text-lg md:text-2xl text-gray-300 font-semibold mb-8 drop-shadow">
              Your voice-powered programming assistant. Ask for code, explanations, and more!
            </p>
          </header>

          {/* Scrollable chat container */}
          <section
            className="flex-1 min-h-0 overflow-y-auto pr-2 pb-2"
            style={{ marginBottom: '96px' }} // space for Input Bar
          >
            {(section === 'home' || section === 'new') && (
              <div className="flex flex-col gap-2">
                {chat.map((msg, idx) => (
                  <ChatBubble key={idx} text={msg.text} type={msg.type} />
                ))}
                <div ref={chatEndRef} />
              </div>
            )}
            {section === 'history' && (
              <div className="flex flex-col gap-2 bg-gray-900 bg-opacity-80 rounded-xl shadow-2xl p-8">
                <h2 className="text-xl font-bold text-white mb-4">Chat History</h2>
                {chat.length === 0 ? (
                  <p className="text-gray-400">No history yet.</p>
                ) : (
                  chat.map((msg, idx) => (
                    <ChatBubble key={idx} text={msg.text} type={msg.type} />
                  ))
                )}
              </div>
            )}
            {section === 'about' && (
              <div className="flex flex-col gap-2 bg-gray-900 bg-opacity-80 rounded-xl shadow-2xl p-8">
                <h2 className="text-xl font-bold text-white mb-4">About EchoCode.ai</h2>
                <p className="text-gray-300">
                  EchoCode.ai is your voice-powered programming assistant. Ask for code, explanations, and more using natural speech!
                </p>
              </div>
            )}
          </section>
        </div>

        {/* Input bar fixed at bottom */}
        {(section === 'home' || section === 'new') && (
          <div
            className="fixed bottom-4 right-4 left-0 flex items-center gap-2 bg-gray-800 bg-opacity-90 p-3 rounded-full shadow-xl z-50 max-w-4xl backdrop-blur-sm border border-gray-600 mx-auto"
            style={{
              marginLeft: sidebarOpen ? SIDEBAR_WIDTH : 0,
              transition: 'margin-left 0.5s',
              height: 56,
            }}
          >
            <label htmlFor="file-upload" className="cursor-pointer p-2 rounded-full hover:bg-gray-700 transition-colors duration-300 animate-pulse">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13" />
              </svg>
              <input id="file-upload" type="file" className="hidden" onChange={handleFileInput} />
            </label>
            <div className="flex-1 flex items-center bg-gray-700 rounded-full px-4 py-2 border border-gray-600">
              <input
                type="text"
                value={textInput}
                onChange={(e) => setTextInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleTextInput()}
                placeholder="Type your message here..."
                className="flex-1 bg-transparent border-none outline-none text-white placeholder-gray-400"
                disabled={loading}
              />
              <button
                onClick={handleTextInput}
                disabled={loading || !textInput.trim()}
                className="ml-2 p-1 rounded-full hover:bg-gray-600 transition-colors duration-300 disabled:opacity-50"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 5l7 7-7 7M5 5l7 7-7 7" />
                </svg>
              </button>
            </div>
            <div className="ml-2">
              <VoiceRecorderButton onVoiceInput={handleVoiceInput} loading={loading} floating />
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
