import React, { useState, useRef, useEffect } from 'react';
// Assuming these are in a components folder, paths are unchanged.
// import Sidebar from './components/Sidebar';
// import VoiceRecorderButton from './components/VoiceRecorderButton';

// Mock components for demonstration since they were not provided.
// You should use your actual components.
const Sidebar = ({ open, onToggle, sections, onSectionClick, activeSection }) => (
  <div className={`transition-all duration-300 text-white bg-gray-900 ${open ? 'w-64' : 'w-20'}`}>
    <button onClick={onToggle} className="p-4 w-full text-left">Toggle</button>
    <nav>
      {sections.map(section => (
        <button key={section.key} onClick={() => onSectionClick(section.key)} className={`block w-full text-left p-4 ${activeSection === section.key ? 'bg-blue-600' : ''}`}>
          {section.label}
        </button>
      ))}
    </nav>
  </div>
);

const VoiceRecorderButton = ({ loading, floating }) => (
  <button className={`w-24 h-24 rounded-full flex items-center justify-center transition-all duration-300 ${loading ? 'bg-yellow-500 animate-pulse' : 'bg-blue-600 hover:bg-blue-700'} ${floating ? 'shadow-2xl' : ''}`}>
    <svg className="w-10 h-10 text-white" fill="currentColor" viewBox="0 0 20 20"><path d="M7 4a3 3 0 016 0v6a3 3 0 11-6 0V4z"></path><path fillRule="evenodd" d="M5.5 8.5A.5.5 0 016 8v2a4 4 0 008 0V8a.5.5 0 011 0v2a5 5 0 01-4.758 4.95l-.242.012A5 5 0 015 10V8a.5.5 0 01.5-.5z" clipRule="evenodd"></path></svg>
  </button>
);


function ChatBubble({ text, type }) {
  return (
    <div className={`flex ${type === 'user' ? 'justify-end' : 'justify-start'} mb-4`}>
      <div className={`max-w-xl px-5 py-3 rounded-2xl shadow-lg text-base font-medium break-words ${type === 'user' ? 'bg-blue-600 text-white' : 'bg-gray-800 text-gray-100'} animate-fade-in`} style={{ fontFamily: 'Poppins, sans-serif' }}>
        {text}
      </div>
    </div>
  );
}

const SIDEBAR_SECTIONS = [
  { key: 'home', label: 'Home' },
  { key: 'new', label: 'New Chat' },
  { key: 'history', label: 'History' },
  { key: 'about', label: 'About' },
];

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [chat, setChat] = useState(() => {
    try {
      const savedChat = localStorage.getItem('echocode_chat');
      return savedChat ? JSON.parse(savedChat) : [];
    } catch (error) {
      return [];
    }
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [section, setSection] = useState('home');
  const chatEndRef = useRef(null);

  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
    localStorage.setItem('echocode_chat', JSON.stringify(chat));
  }, [chat]);

  const handleVoiceInput = async (audioBlob) => {
    // This function remains the same as your provided code.
    // For this example, we'll simulate a conversation flow.
    setLoading(true);
    setError('');
    const userText = "This is a simulated user voice input.";
    setChat((prev) => [...prev, { type: 'user', text: userText }]);

    setTimeout(() => {
        const aiText = "This is a simulated response from the AI, demonstrating how the chat window grows and the page scrolls.";
        setChat((prev) => [...prev, { type: 'ai', text: aiText }]);
        setLoading(false);
    }, 1500);
  };

  const handleSectionClick = (key) => {
    setSection(key);
    if (key === 'new') {
      setChat([]);
      localStorage.setItem('echocode_chat', JSON.stringify([]));
    }
  };

  return (
    <div className="flex min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-700 text-white" style={{ fontFamily: 'Poppins, sans-serif' }}>
      <Sidebar 
        open={sidebarOpen} 
        onToggle={() => setSidebarOpen((o) => !o)} 
        sections={SIDEBAR_SECTIONS} 
        onSectionClick={handleSectionClick} 
        activeSection={section} 
      />

      {/* Main content area now handles its own scrolling and has padding at the bottom for the fixed button */}
      <main className="flex-1 flex flex-col pb-40 relative">
        {/* This wrapper handles the initial vertical centering and transitions to the top */}
        <div className={`w-full flex flex-col items-center transition-all duration-700 px-4 ${chat.length === 0 ? 'flex-grow justify-center' : 'pt-8 pb-2'}`}>
          <img src="https://placehold.co/80x80/7c3aed/ffffff?text=EC" alt="App Icon" className="w-16 h-16 md:w-20 md:h-20 mb-4 drop-shadow-lg rounded-2xl" />
          <h1 className="text-3xl md:text-5xl font-extrabold text-white mb-2 tracking-tight drop-shadow-lg text-center">EchoCode.ai</h1>
          <p className="text-lg md:text-2xl text-gray-300 font-semibold mb-8 drop-shadow text-center">Your voice-powered programming assistant. Ask for code, explanations, and more!</p>
        </div>

        {/* Chat container now has a responsive width and no scroll/height constraints, allowing the page to grow */}
        {(section === 'home' || section === 'new') && (
          <div className="w-11/12 md:w-3/4 mx-auto flex flex-col gap-2">
            {chat.map((msg, idx) => (
              <ChatBubble key={idx} text={msg.text} type={msg.type} />
            ))}
            <div ref={chatEndRef} />
          </div>
        )}

        {/* Other sections also use the new responsive width */}
        {section === 'history' && (
          <div className="w-11/12 md:w-3/4 mx-auto flex flex-col gap-2 bg-gray-900 bg-opacity-80 rounded-xl shadow-2xl p-8 mt-8">
            <h2 className="text-xl font-bold text-white mb-4">Chat History</h2>
            {chat.length === 0 ? <div className="text-gray-400">No history yet.</div> : chat.map((msg, idx) => (
              <ChatBubble key={idx} text={msg.text} type={msg.type} />
            ))}
          </div>
        )}
        {section === 'about' && (
          <div className="w-11/12 md:w-3/4 mx-auto flex flex-col gap-2 bg-gray-900 bg-opacity-80 rounded-xl shadow-2xl p-8 mt-8">
            <h2 className="text-xl font-bold text-white mb-4">About EchoCode.ai</h2>
            <p className="text-gray-300">EchoCode.ai is your voice-powered programming assistant. Ask for code, explanations, and more using natural speech!</p>
          </div>
        )}

        {error && <div className="text-red-500 mt-4 text-center font-bold">{error}</div>}

        {/* The microphone button is now in a fixed container that respects the sidebar's width */}
        {(section === 'home' || section === 'new') && (
          <div className={`fixed bottom-0 z-50 transition-all duration-300 right-0 ${sidebarOpen ? 'left-64' : 'left-20'}`}>
            <div className="flex justify-center pb-10">
              <VoiceRecorderButton onVoiceInput={handleVoiceInput} loading={loading} floating />
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
